import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { fetchMovieDetails } from "../api";
import styles from './MovieDetails.module.css';

export const MovieDetails = () => {
  const { movieId } = useParams();
  const [movie, setMovie] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchMovieDetails(movieId).then(setMovie);
  }, [movieId]);

  if (!movie) return <p>Loading...</p>;

  return (
    <div className={styles.container}>
      <button onClick={() => navigate(-1)} className={styles.backButton}>Come Back</button>
      <h2 className={styles.movieTitle}>{movie.title} ({movie.release_date.slice(0, 4)})</h2>
      <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
      <p className={styles.movieOverview}>{movie.overview}</p>
      <p className={styles.movieGenres}>Genres: {movie.genres.map(g => g.name).join(", ")}</p>
    </div>
  );
};
