const API_KEY = 'cc87d95dbbb46c6d0da6f726f3495047';
// src/components/api.jsx

// Funcție pentru a obține filmele populare
export const fetchTrendingMovies = async () => {
  const response = await fetch(`https://api.themoviedb.org/3/trending/movie/day?api_key=${API_KEY}`);
  if (!response.ok) {
    throw new Error('Failed to fetch trending movies');
  }
  return await response.json();
};

// Funcție pentru a căuta filme după cuvânt cheie
export const searchMovies = async (query) => {
  const response = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=${query}`);
  if (!response.ok) {
    throw new Error('Failed to fetch movies');
  }
  return await response.json();
};

// Funcție pentru a obține detalii despre un film
export const fetchMovieDetails = async (movieId) => {
  const response = await fetch(`https://api.themoviedb.org/3/movie/${movieId}?api_key=${API_KEY}`);
  if (!response.ok) {
    throw new Error('Failed to fetch movie details');
  }
  return await response.json();
};

// Funcție pentru a obține informații despre distribuție
export const fetchMovieCredits = async (movieId) => {
  const response = await fetch(`https://api.themoviedb.org/3/movie/${movieId}/credits?api_key=${API_KEY}`);
  if (!response.ok) {
    throw new Error('Failed to fetch movie credits');
  }
  return await response.json();
};

// Funcție pentru a obține recenzii
export const fetchMovieReviews = async (movieId) => {
  const response = await fetch(`https://api.themoviedb.org/3/movie/${movieId}/reviews?api_key=${API_KEY}`);
  if (!response.ok) {
    throw new Error('Failed to fetch movie reviews');
  }
  return await response.json();
};
